import { useState, useEffect } from "react";
import axios from "axios";

const PaymentForm = () => {
  const [students, setStudents] = useState([]);
  const [studentIdInput, setStudentIdInput] = useState("");
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [selectedInstallment, setSelectedInstallment] = useState(null);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/students");
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };
    fetchStudents();
  }, []);

  const handleFindStudent = () => {
    const student = students.find((s) => s._id === studentIdInput.trim());
    if (student) {
      setSelectedStudent(student);
      setEmail(student.contactEmail);
      setPhone(student.contactPhone);
      setAmount("");
      setSelectedInstallment(null);
      setMessage("");
    } else {
      setSelectedStudent(null);
      setMessage("Student not found!");
    }
  };

  const handleInstallmentChange = (e) => {
    const installment = selectedStudent?.feeStructure?.installments.find(
      (inst) => inst._id === e.target.value
    );
    setSelectedInstallment(installment);
    setAmount(installment?.amount);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Processing...");

    try {
      const response = await axios.post(
        "http://localhost:5000/api/students/create-payment-link",
        { email, phone, amount }
      );

      if (response.data.success) {
        setMessage(`✅ Payment link sent to ${email} and WhatsApp: +91${phone}`);
      } else {
        setMessage("❌ Failed to send payment link.");
      }
    } catch (error) {
      setMessage("❌ Error: " + error.message);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-center mb-6">Send Payment Link</h2>
      <form onSubmit={handleSubmit} className="space-y-5">

        {/* Student ID */}
        <div>
          <label className="block mb-2 font-medium">Enter Student ID:</label>
          <div className="flex">
            <input
              type="text"
              className="flex-1 border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Paste student ID here"
              value={studentIdInput}
              onChange={(e) => setStudentIdInput(e.target.value)}
              required
            />
            <button
              type="button"
              onClick={handleFindStudent}
              className="ml-3 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
            >
              Find Student
            </button>
          </div>
        </div>

        {/* Installment Dropdown */}
        {selectedStudent && (
          <div>
            <label className="block mb-2 font-medium">Select Installment:</label>
            <select
              onChange={handleInstallmentChange}
              required
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select Installment</option>
              {selectedStudent.feeStructure?.installments.map((inst) => (
                <option key={inst._id} value={inst._id}>
                  Installment {inst.installmentNo} - ₹{inst.amount}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Auto-filled fields */}
        <div>
          <label className="block mb-2 font-medium">Email:</label>
          <input
            type="email"
            value={email}
            readOnly
            className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-gray-100"
          />
        </div>

        <div>
          <label className="block mb-2 font-medium">WhatsApp Number:</label>
          <input
            type="tel"
            value={phone}
            readOnly
            className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-gray-100"
          />
        </div>

        <div>
          <label className="block mb-2 font-medium">Amount (INR):</label>
          <input
            type="number"
            value={amount}
            readOnly
            className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-gray-100"
          />
        </div>

        <button
          type="submit"
          disabled={!amount}
          className={`w-full py-3 text-white font-semibold rounded-lg ${
            amount ? "bg-green-600 hover:bg-green-700" : "bg-gray-400 cursor-not-allowed"
          }`}
        >
          Send Payment Link
        </button>
      </form>

      {message && <p className="mt-5 text-center font-semibold">{message}</p>}
    </div>
  );
};

export default PaymentForm;
