import { useState } from "react";
import axios from "axios";

const PaymentForm = () => {
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [amount, setAmount] = useState("");
    const [message, setMessage] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage("Processing...");

        try {
            const response = await axios.post("http://localhost:5000/api/create-payment-link", {
                email,
                phone,
                amount,
            });

            if (response.data.success) {
                setMessage(`Payment link sent successfully to ${email} and WhatsApp: +91${phone}`);
            } else {
                setMessage("Failed to send payment link.");
            }
        } catch (error) {
            setMessage("Error: " + error.message);
        }
    };

    return (
        <div style={styles.container}>
            <h2>Send Payment Link</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.inputGroup}>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Enter email"
                    />
                </div>
                <div style={styles.inputGroup}>
                    <label>WhatsApp Number:</label>
                    <input
                        type="tel"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                        placeholder="Enter WhatsApp number"
                    />
                </div>
                <div style={styles.inputGroup}>
                    <label>Amount (INR):</label>
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        required
                        placeholder="Enter amount"
                    />
                </div>
                <button type="submit" style={styles.button}>Send Payment Link</button>
            </form>
            <p style={styles.message}>{message}</p>
        </div>
    );
};

const styles = {
    container: { textAlign: "center", marginTop: "50px" },
    form: { display: "inline-block", textAlign: "left", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" },
    inputGroup: { marginBottom: "10px" },
    button: { backgroundColor: "#28a745", color: "#fff", padding: "10px", border: "none", borderRadius: "5px", cursor: "pointer" },
    message: { marginTop: "20px", fontWeight: "bold" }
};

export default PaymentForm;
