import { useState } from "react";
import axios from "axios";
import { FaSearch, FaUser, FaEnvelope, FaWhatsapp, FaRupeeSign } from "react-icons/fa";

const PaymentForm = () => {
  const [studentIdInput, setStudentIdInput] = useState("");
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  const handleFindStudent = async () => {
    setMessage("Searching...");
    try {
      const response = await axios.get(
        `http://localhost:5000/api/students/${studentIdInput.trim()}`
      );
      const student = response.data;
      setSelectedStudent(student);
      setEmail(student.contactEmail);
      setPhone(student.contactPhone);

      const firstInstallment = student.feeStructure?.installments?.[0];
      if (firstInstallment) {
        setAmount(firstInstallment.amount);
      } else {
        setAmount("");
      }

      setMessage("✅ Student Found");
    } catch (error) {
      console.error("Error fetching student:", error);
      setSelectedStudent(null);
      setMessage("❌ Student not found!");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Processing...");

    try {
      const response = await axios.post(
        "http://localhost:5000/api/students/create-payment-link",
        { email, phone, amount }
      );

      if (response.data.success) {
        setMessage(`✅ Payment link sent to ${email} and WhatsApp: +91${phone}`);
      } else {
        setMessage("❌ Failed to send payment link.");
      }
    } catch (error) {
      setMessage("❌ Error: " + error.message);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-gradient-to-br from-blue-50 to-purple-50 shadow-2xl rounded-2xl">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        Send Payment Link
      </h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Student ID */}
        <div>
          <label className="block mb-2 font-semibold text-gray-700">
            Enter Student ID:
          </label>
          <div className="flex items-center">
            <input
              type="text"
              className="flex-1 border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
              placeholder="Enter student ID"
              value={studentIdInput}
              onChange={(e) => setStudentIdInput(e.target.value)}
              required
            />
            <button
              type="button"
              onClick={handleFindStudent}
              className="ml-3 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center justify-center transition-all duration-300"
            >
              <FaSearch className="mr-2" /> Find Student
            </button>
          </div>
        </div>

        {/* Auto-filled fields */}
        <div>
          <label className=" mb-2 font-semibold text-gray-700 flex items-center">
            <FaEnvelope className="mr-2" /> Email:
          </label>
          <input
            type="email"
            value={email}
            readOnly
            className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
          />
        </div>

        <div>
          <label className=" mb-2 font-semibold text-gray-700 flex items-center">
            <FaWhatsapp className="mr-2" /> WhatsApp Number:
          </label>
          <input
            type="tel"
            value={phone}
            readOnly
            className="w-full border border-gray-300 rounded-lg px-4 py-2 bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
          />
        </div>

        <div>
          <label className=" mb-2 font-semibold text-gray-700 flex items-center">
            <FaRupeeSign className="mr-2" /> Amount (INR):
          </label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
          />
        </div>

        <button
          type="submit"
          disabled={!amount}
          className={`w-full py-3 text-white font-semibold rounded-lg flex items-center justify-center transition-all duration-300 ${
            amount ? "bg-green-600 hover:bg-green-700" : "bg-gray-400 cursor-not-allowed"
          }`}
        >
          Send Payment Link
        </button>
      </form>

      {message && (
        <p className="mt-5 text-center font-semibold text-gray-700">{message}</p>
      )}
    </div>
  );
};

export default PaymentForm;
