import { useState, useEffect } from "react";
import axios from "axios";

const PaymentForm = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [selectedInstallment, setSelectedInstallment] = useState(null);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  // Fetch student data from database
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/students");
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };
    fetchStudents();
  }, []);

  // Update fields when student changes
  const handleStudentChange = (e) => {
    const student = students.find((s) => s._id === e.target.value);
    setSelectedStudent(student);
    setEmail(student.contactEmail);
    setPhone(student.contactPhone);
    setAmount(""); // Reset amount until installment is selected
    setSelectedInstallment(null);
  };

  // Update amount when installment changes
  const handleInstallmentChange = (e) => {
    const installment = selectedStudent?.feeStructure?.installments.find(
      (inst) => inst._id === e.target.value
    );
    setSelectedInstallment(installment);
    setAmount(installment?.amount);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Processing...");

    try {
      const response = await axios.post(
        "http://localhost:5000/api/students/create-payment-link",
        { email, phone, amount }
      );

      if (response.data.success) {
        setMessage(`Payment link sent successfully to ${email} and WhatsApp: +91${phone}`);
      } else {
        setMessage("Failed to send payment link.");
      }
    } catch (error) {
      setMessage("Error: " + error.message);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Send Payment Link</h2>
      <form onSubmit={handleSubmit} style={styles.form}>

        {/* Student Dropdown */}
        <div style={styles.inputGroup}>
          <label>Select Student:</label>
          <select onChange={handleStudentChange} required>
            <option value="">Select Student</option>
            {students.map((student) => (
              <option key={student._id} value={student._id}>
                {student.studentName} ({student.rollNo})
              </option>
            ))}
          </select>
        </div>

        {/* Installment Dropdown */}
        {selectedStudent && (
          <div style={styles.inputGroup}>
            <label>Select Installment:</label>
            <select onChange={handleInstallmentChange} required>
              <option value="">Select Installment</option>
              {selectedStudent.feeStructure?.installments.map((inst) => (
                <option key={inst._id} value={inst._id}>
                  Installment {inst.installmentNo} - â‚¹{inst.amount}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Auto-filled fields */}
        <div style={styles.inputGroup}>
          <label>Email:</label>
          <input type="email" value={email} readOnly />
        </div>

        <div style={styles.inputGroup}>
          <label>WhatsApp Number:</label>
          <input type="tel" value={phone} readOnly />
        </div>

        <div style={styles.inputGroup}>
          <label>Amount (INR):</label>
          <input type="number" value={amount} readOnly />
        </div>

        <button type="submit" style={styles.button} disabled={!amount}>Send Payment Link</button>
      </form>

      <p style={styles.message}>{message}</p>
    </div>
  );
};

const styles = {
  container: { textAlign: "center", marginTop: "50px" },
  form: { display: "inline-block", textAlign: "left", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" },
  inputGroup: { marginBottom: "15px" },
  button: { backgroundColor: "#28a745", color: "#fff", padding: "10px 15px", border: "none", borderRadius: "5px", cursor: "pointer" },
  message: { marginTop: "20px", fontWeight: "bold" }
};

export default PaymentForm;
